print("part 1: {}\npart 2: {}".format(*(list(map(sum, zip(*((lambda p: ([p.append([p[-1][i] - p[-1][i - 1] for i in range(1, len(p[-1]))]) for _ in range(len(p[-1]) - 1) if any(p[-1])], [p := p[:-2] + [[p[-2][0] - p[-1][0]] + [p[-1][-1] + p[-2][-1]]] for _ in range(len(p) - 1, 0, -1)])[1][-1][0])([h]) for h in [[int(n) for n in l.split()] for l in open("input.txt").read().split("\n") if l.strip()]))))[::-1])))